import { useState } from "react";

const BouncingArrow = () => {
  const [isBouncing, setIsBouncing] = useState(false);

  const handleToggleBounce = () => {
    setIsBouncing(!isBouncing);
  };

  return (
    <div className="flex items-center justify-center  ">
      <div
        className="relative flex items-center justify-center w-7 h-7 border-2 border-white rounded-full cursor-pointer hover:animate-pulse"
        onClick={handleToggleBounce}
      >
        <div className="absolute flex  items-center ">
          <span
            className={`w-2 h-2 bg-no-repeat bg-center bg-contain transform animate-bounceAlpha ${
              isBouncing ? "animate-bounceAlpha" : ""
            }`}
            style={{
              backgroundImage: `url('data:image/svg+xml;base64,')`,
            }}
          />
          <span
            className={`w-3 h-3 bg-no-repeat bg-center bg-contain transform animate-bounceAlpha${
              isBouncing ? "animate-bounceAlpha" : ""
            }`}
            style={{
              backgroundImage: `url('data:image/svg+xml;base64,')`,
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default BouncingArrow;
